C#<pre><code>// Program.cs (.NET 8/9)
// - استدعاء REST خارجي مع مهلة ومحاولات وIdempotency-Key.
// - نقطة Webhook مع تحقق HMAC للتوقيع وتصفية التكرار.

using System.Net.Http.Headers;
using System.Security.Cryptography;
using System.Text;
using Microsoft.Extensions.Caching.Memory;

var builder = WebApplication.CreateBuilder(args);
builder.Services.AddMemoryCache();
var app = builder.Build();

var http = new HttpClient { BaseAddress = new Uri("https://api.example.com"), Timeout = TimeSpan.FromSeconds(5) };

// 1) استدعاء واجهة خارجية (Pull)
app.MapPost("/pay", async (IMemoryCache cache) =&gt;
{
    var body = """{"amount":120,"currency":"USD"}""";
    string idem = Guid.NewGuid().ToString("N");            // مفتاح Idempotency
    var req = new HttpRequestMessage(HttpMethod.Post, "/v1/payments");
    req.Content = new StringContent(body, Encoding.UTF8, "application/json");
    req.Headers.Add("Idempotency-Key", idem);
    req.Headers.Authorization = new AuthenticationHeaderValue("Bearer", Environment.GetEnvironmentVariable("API_TOKEN"));

    for (int attempt = 1; attempt &lt;= 3; attempt++)
    {
        using var res = await http.SendAsync(req);
        if (res.IsSuccessStatusCode) return Results.Text(await res.Content.ReadAsStringAsync(), "application/json");
        if ((int)res.StatusCode is 429 or &gt;= 500) await Task.Delay(TimeSpan.FromMilliseconds(200 * Math.Pow(2, attempt)));
        else return Results.StatusCode((int)res.StatusCode);
    }
    return Results.StatusCode(504); // Gateway Timeout
});

// 2) استقبال Webhook (Push) مع HMAC-SHA256
app.MapPost("/webhooks/provider", async (HttpRequest request, IMemoryCache cache) =&gt;
{
    // اقرأ الجسم كاملًا
    using var reader = new StreamReader(request.Body, Encoding.UTF8);
    var payload = await reader.ReadToEndAsync();

    // تحقق التوقيع
    var sig = request.Headers["X-Signature"].ToString();    // مثال: sha256=HEX
    var secret = Environment.GetEnvironmentVariable("WEBHOOK_SECRET") ?? "dev-secret";
    using var hmac = new HMACSHA256(Encoding.UTF8.GetBytes(secret));
    var hash = Convert.ToHexString(hmac.ComputeHash(Encoding.UTF8.GetBytes(payload))).ToLowerInvariant();
    var expected = "sha256=" + hash;

    if (!CryptographicOperations.FixedTimeEquals(Encoding.UTF8.GetBytes(sig), Encoding.UTF8.GetBytes(expected)))
        return Results.Unauthorized();

    // مانع تكرار بسيط (Idempotency)
    var eventId = request.Headers["X-Event-Id"].ToString();
    if (string.IsNullOrEmpty(eventId) || !cache.Set(eventId, true, TimeSpan.FromMinutes(10)))
        return Results.Ok(); // مكرّر — تجاهل بهدوء

    // TODO: عالج الحدث (مثلاً تحديث حالة الدفع)
    Console.WriteLine($"Webhook OK: {eventId}");
    return Results.Ok();
});

app.Run();
</code></pre> <p>الفكرة: - Outbound: مهلة + محاولات تدريجية + Idempotency-Key. - Inbound: تحقّق توقيع HMAC + منع تكرار + استجابة سريعة 2xx.</p>

C#<pre><code>// Program.cs  (.NET 8/9)
// dotnet new web -n PipelineDemo &amp;&amp; cd PipelineDemo &amp;&amp; dotnet run
using System.Diagnostics;

var builder = WebApplication.CreateBuilder(args);

// تسجيل Middlewares تعتمد DI (اختياري)
builder.Services.AddSingleton&lt;RequestTimingMiddleware&gt;();
builder.Services.AddSingleton&lt;CorrelationIdMiddleware&gt;();

var app = builder.Build();

// 1) معالجة الأخطاء في أعلى السلسلة
app.Use(async (ctx, next) =&gt;
{
    try { await next(); }
    catch (Exception ex)
    {
        Console.Error.WriteLine($"[ERR] {ex.Message}");
        ctx.Response.StatusCode = 500;
        await ctx.Response.WriteAsJsonAsync(new { error = "server_error" });
    }
});

// 2) معرّف ترابط للطلب (قبل أي تسجيل)
app.UseMiddleware&lt;CorrelationIdMiddleware&gt;();

// 3) فرع داخلي لمسارات /internal فقط (UseWhen لا يغيّر المسار)
app.UseWhen(ctx =&gt; ctx.Request.Path.StartsWithSegments("/internal"), branch =&gt;
{
    branch.Use(async (ctx, next) =&gt;
    {
        // حماية بسيطة: مفتاح داخلي
        if (ctx.Request.Headers["X-Internal-Key"] != "secret")
        {
            ctx.Response.StatusCode = 401;      // Short-circuit
            await ctx.Response.WriteAsync("unauthorized");
            return;
        }
        await next();
    });
});

// 4) قياس زمن الطلب (قبل/بعد next)
app.UseMiddleware&lt;RequestTimingMiddleware&gt;();

// 5) نقطة صحّة Terminal سريعة (Map = Endpoint نهائي)
app.MapGet("/healthz", () =&gt; Results.Ok(new { status = "ok" }));

// 6) API عادي
var api = app.MapGroup("/api");
api.MapGet("/hello", (HttpContext ctx) =&gt;
{
    // رمي خطأ تجريبي لترى المعالج بالأعلى
    if (ctx.Request.Query.ContainsKey("boom")) throw new Exception("demo");
    return Results.Ok(new { msg = "hello", cid = ctx.Response.Headers["X-Correlation-Id"].ToString() });
});

// 7) فرع لمسار ثابت (MapWhen يوجّه بقية السلسلة)
app.MapWhen(ctx =&gt; ctx.Request.Path.StartsWithSegments("/internal"), branch =&gt;
{
    branch.MapGet("/internal/info", (HttpContext ctx) =&gt;
        Results.Ok(new { env = app.Environment.EnvironmentName }));
});

app.Run();

// ======= Middlewares مخصّصة =======
public class CorrelationIdMiddleware
{
    private const string Header = "X-Correlation-Id";
    private readonly RequestDelegate _next;
    public CorrelationIdMiddleware(RequestDelegate next) =&gt; _next = next;

    public async Task InvokeAsync(HttpContext ctx)
    {
        var cid = ctx.Request.Headers.TryGetValue(Header, out var v) &amp;&amp; !string.IsNullOrWhiteSpace(v)
            ? v.ToString()
            : Guid.NewGuid().ToString("N");

        ctx.Response.Headers[Header] = cid;         // قبل next: تجهيز السياق
        await _next(ctx);                            // مرّر للذي بعده
        // بعد next: يمكن إضافة Headers/Logging اعتمادًا على النتيجة
    }
}

public class RequestTimingMiddleware
{
    private readonly RequestDelegate _next;
    public RequestTimingMiddleware(RequestDelegate next) =&gt; _next = next;

    public async Task InvokeAsync(HttpContext ctx)
    {
        var sw = Stopwatch.StartNew();
        await _next(ctx);
        sw.Stop();
        ctx.Response.Headers["X-Elapsed-ms"] = sw.ElapsedMilliseconds.ToString();
        Console.WriteLine($"{ctx.Request.Method} {ctx.Request.Path} -&gt; {ctx.Response.StatusCode} in {sw.ElapsedMilliseconds}ms");
    }
}
</code></pre> <p>جرّب سريعًا: Bash<pre><code>curl -i http://localhost:5000/healthz
curl -i http://localhost:5000/api/hello
curl -i "http://localhost:5000/api/hello?boom=1"      # سيُلتقط الاستثناء في الأعلى
curl -i http://localhost:5000/internal/info           # 401
curl -i -H "X-Internal-Key: secret" http://localhost:5000/internal/info
</code></pre></p> <p>الملاحظة: تَظهر Headers مثل <code>X-Correlation-Id</code> و<code>X-Elapsed-ms</code>؛ الطلبات الداخلية تتوقّف مبكّرًا إن لم تحمل المفتاح (Short-circuit).</p>

